import AsyncStorage from "@react-native-async-storage/async-storage";
import { Product } from "../interfaces/product";
import { BancoSql } from "../core/db";

const state: any = {
  products: [],
};
const db = BancoSql();

async function validation(params: Product) {
  const result = list();
  console.log(params);
  result.then((result: Product) => {
    console.log(result.id == params.id),
      console.log(result.id),
      console.log(params.id);
  });

  list();
}

export async function list() {
  await db.transaction((tx) => {
    tx.executeSql(
      "select * from car",
      [],
      (_, { rows }) => (state.products = rows)
    );
  });
  return state.products;
}
export async function update(params: Product) {
  await db.transaction((tx) => {
    tx.executeSql(
      "update car set name = ?, " +
        " name = ?, image = ?, price = ?,stock = ?,createdAt = ?" +
        " where id = ?;",
      [
        params.name.toString(),
        params.image.toString(),
        params.price,
        params.stock,
        params.createdAt.toString(),
        params.id,
      ]
    );
  });
}

export async function add(params: Product) {
  validation(params);
  const add = {
    id: params.id,
    name: params.name,
    price: params.price,
    image: params.image,
    stock: 1,
  };
  if (params.id === null) {
    return false;
  }
  db.transaction((tx) => {
    tx.executeSql(
      "insert into car (id, name,image, price,stock,createdAt) values (?,?,?,?,?,?)",
      [
        params.id,
        params.name.toString(),
        params.image.toString(),
        params.price,
        params.stock,
        params.createdAt.toString(),
      ]
    );
  });
}
