import AsyncStorage from "@react-native-async-storage/async-storage";
import { Product } from "../interfaces/product";

const state = {
  products: [],
};

export async function save(params: Product) {
  const pord: any = [...state.products];
  validation(params, state.products);
  const add = {
    id: params.id,
    name: params.name,
    price: params.price,
    image: params.image,
    stock: 1,
  };
  pord.splice(0, 0, add);
  console.log(pord.id);

  await AsyncStorage.setItem("car", JSON.stringify(pord));
}

export async function load() {
  const result: any = await AsyncStorage.getItem("car");
  state.products = JSON.parse(result);
}

async function validation(params: any, prod: any) {
  prod.forEach((item: any) => {
    if (params.id === item.id) {
      console.log("Produto j치 existe");

      alert("Produto j치 existe");
    }
  });
}
