import * as SQLite from "expo-sqlite";
import path from "path";
import { Platform } from "react-native";
export function _openDatabase() {
  const db = SQLite.openDatabase(`${path.isAbsolute("/src/core/db/")}db.db`);
  console.log("caminho: ", db);
  if (Platform.OS === "web") {
    return {
      transaction: () => {
        return {
          executeSql: () => {},
        };
      },
    };
  }
  return db;
}

export function Db(db: any) {
  db.transaction((tx: any) => {
    tx.executeSql(
      "create table if not exists car (id integer," +
        "name text, image text, price text, stock text, createdAt text );"
    );
  });
}

export function BancoSql() {
  const db = _openDatabase();
  Db(db);
  return db;
}
