import { Product } from "../interfaces/product";
import { BancoSql } from "../core/db";
import React, { useEffect, useState } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";

const state: any = {
  products: [],
  status: "",
  items: [],
};
const db = BancoSql();

async function validation(params: Product) {
  const result = await list();
  console.log(result);
  for (let index = 0; index < result?.length; index++) {
    console.log(result);
    if (result[index].id == params.id) {
      state.status = "true";
      error("Produto ja esta no carinho");
      return state.status;
    } else {
      state.status = "false";
      success("Produto add no carinho");
      return state.status;
    }
  }
}

export async function list() {
  try {
    state.products = await AsyncStorage.getItem("car");
    return JSON.parse(state.products);
  } catch (e) {
    // error reading value
  }

  return state.products;
}
export async function update(params: Product) {
  await db.transaction((tx) => {
    tx.executeSql(
      "update car set name = ?, " +
        " name = ?, image = ?, price = ?,stock = ?,createdAt = ?" +
        " where id = ?;",
      [
        params.name.toString(),
        params.image.toString(),
        params.price,
        params.stock,
        params.createdAt.toString(),
        params.id,
      ]
    );
  });
}

export async function add(params: Product) {
  let validate: any = await validation(params);
  console.log(validate);

  if (validate == "true") return;

  let add = {
    id: params.id,
    name: params.name,
    price: params.price,
    image: params.image,
    stock: 1,
  };
  const res = state.products;
  try {
    res.splice(0, 0, JSON.stringify(add));
    console.log(res);
  } catch (error) {
    console.log(error);
  }

  console.log(res);

  if (params.id === null) {
    return false;
  }

  try {
    const jsonValue = JSON.stringify(add);
    await AsyncStorage.setItem("car", jsonValue);
  } catch (e) {
    console.log(e);
  }
}
function error(msg: String) {
  console.log(msg);
}
function success(msg: String) {}
