import React, { useEffect, useState } from "react";
import { View, Text, ScrollView, StyleSheet } from "react-native";
import { Product } from "../../interfaces/product";
import { list } from "../../service";
import Cart from "../../component/cart";

const state: any = {
  products: [],
};
export default function index() {
  const [product, setProducts] = useState<Product[]>([]);
  let teste: any = [];
  function handleLoad() {
    list().then((data) => setProducts(data));

    //console.log(teste);
    console.log(product);
    // teste.map((e: any, index: number) => console.log(e[0]));
  }
  useEffect(() => {
    handleLoad();
  }, []);
  return (
    <View>
      <ScrollView>
        <View style={styles.viewScroll}>
          {teste.map((r: Product) => (
            <Cart key={r.id} img={r.image} text={`"R$ ${r.price}"`}>
              {r.name}
            </Cart>
          ))}
        </View>
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  viewScroll: {},
});
