import * as SQLite from "expo-sqlite";
import path from "path";
export function _openDatabase() {
  const caminho = path.format({
    root: "/",
    dir: "/src/core/db",
    base: "db.db",
  });
  console.log("caminho: ", caminho);
  const db = SQLite.openDatabase(`${path.basename("./")}db.db`);

  return db;
}

export function Db() {
  const db = _openDatabase();
  db.transaction((tx) => {
    tx.executeSql(
      "create table if not exists car (id integer," +
        "name text, image text, price text, stock text, createdAt text );"
    );
  });
}

export function BancoSql() {
  const db = _openDatabase();
  Db();
  return db;
}
