import React, { useEffect, useState } from "react";
import {
  Button,
  Image,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";
import { Product } from "../../interfaces/product";
import { update } from "../../service";

export default function Index(props: any) {
  const [qtde, setQtde] = useState();

  function filterDesc(desc: any) {
    if (desc.length < 20) {
      return desc;
    }
    return `${desc.substring(0, 18)}...`;
  }
  async function handleMenos(qtde: any) {
    // await update(dados, stack, id);
    console.log("menos", qtde);
    let valor = (qtde = -1);
    setQtde(valor);
  }
  async function handleMas(qtde: any) {
    // await update(dados, stack, id);
    console.log("mais", (qtde = qtde + 1));
    let valor = (qtde = qtde + 1);
    setQtde(valor);
  }
  useEffect(() => {
    setQtde(props.stock);
  }, []);
  return (
    <View style={styles.container}>
      <View style={styles.img}>
        <Image source={{ uri: props.image }} style={styles.shoesimg} />
      </View>
      <View style={styles.title}>
        <Text style={styles.shoestext}>{filterDesc(props.children)}</Text>
        <Text style={styles.saldo}>{props.price}</Text>
        <View style={{ flexDirection: "row" }}>
          <TouchableOpacity
            onPress={() => handleMenos(qtde)}
            style={styles.buttons}
          >
            <Text>-</Text>
          </TouchableOpacity>
          <TextInput
            style={styles.stock}
            value={qtde}
            onChangeText={(stock) => setQtde(qtde)}
          />
          <TouchableOpacity
            onPress={() => handleMas(qtde)}
            style={styles.buttons}
          >
            <Text>+</Text>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    paddingTop: 20,
  },
  shoestext: {
    fontSize: 16,
  },
  shoesimg: {
    width: 75,
    height: 75,
    borderRadius: 4,
  },

  img: { paddingLeft: 20 },
  title: {
    flexDirection: "column",
    paddingLeft: 20,
  },
  saldo: {
    paddingBottom: 10,
    paddingTop: 10,
    color: "red",
    fontWeight: 300,
  },
  stock: {
    width: "50px",
    height: "30px",
    opacity: 0.5,
    marginLeft: "22px",
    marginRight: "-22px",
  },
  buttons: {
    color: "#000",
    width: "30px",
    height: "30px",
    justifyContent: "center",
    alignItems: "center",
    alignSelf: "center",
    backgroundColor: "#fff",
    borderWidth: 1,

    borderColor: "#b4b4b4",
    fontSize: "30px",
  },
});
