import React, { useState } from "react";
import {
  Button,
  Image,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";
import { Product } from "../../interfaces/product";
import { update } from "../../service";
import { useForm, SubmitHandler } from "react-hook-form";

type Inputs = {
  example: string;
  exampleRequired: string;
};

export default function Index(props: any) {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm<Inputs>();

  const onSubmit: SubmitHandler<Inputs> = (data) => console.log(data);

  function filterDesc(desc: any) {
    if (desc.length < 20) {
      return desc;
    }
    return `${desc.substring(0, 18)}...`;
  }
  async function handleMenos(stack: number) {
    await update(props, stack);
  }
  return (
    <View style={styles.container}>
      <View style={styles.img}>
        <Image source={{ uri: props.img }} style={styles.shoesimg} />
      </View>
      <View style={styles.title}>
        <Text style={styles.shoestext}>{filterDesc(props.children)}</Text>
        <Text style={styles.saldo}>{props.text}</Text>
        <View style={{ flexDirection: "row" }}>
          <TouchableOpacity onPress={() => {}} style={styles.buttons}>
            <Text>-</Text>
          </TouchableOpacity>
          <TextInput style={styles.stock} value={props.stock} />
          <form onSubmit={handleSubmit(onSubmit)}>
            <input {...register("exampleRequired", { required: true })} />

            {errors.exampleRequired && <span>This field is required</span>}

            <input type="number" />
          </form>

          <TouchableOpacity onPress={() => {}} style={styles.buttons}>
            <Text>+</Text>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    paddingTop: 20,
  },
  shoestext: {
    fontSize: 16,
  },
  shoesimg: {
    width: 75,
    height: 75,
    borderRadius: 4,
  },

  img: { paddingLeft: 20 },
  title: {
    flexDirection: "column",
    paddingLeft: 20,
  },
  saldo: {
    paddingBottom: 10,
    paddingTop: 10,
    color: "red",
    fontWeight: 300,
  },
  stock: {
    width: "50px",
    height: "30px",
    opacity: 0.5,
    marginLeft: "22px",
    marginRight: "-22px",
  },
  buttons: {
    color: "#000",
    width: "30px",
    height: "30px",
    justifyContent: "center",
    alignItems: "center",
    alignSelf: "center",
    backgroundColor: "#fff",
    borderWidth: 1,

    borderColor: "#b4b4b4",
    fontSize: "30px",
  },
});
