import { useRoute } from "@react-navigation/native";
import React, { useEffect } from "react";
import {
  Text,
  StyleSheet,
  View,
  ScrollView,
  Image,
  Button,
} from "react-native";
import { Product } from "../../interfaces/product";

export default function Index(props: any) {
  const { name, id, image, price, stock } = props.route.params.item;

  useEffect(() => {
    props.navigation.setOptions({
      headerTitle: name,
    });
  }, []);

  return (
    <View style={styles.container}>
      <View style={{ flex: 1 }}>
        <Image
          source={{ uri: image }}
          style={styles.image}
          resizeMode="stretch"
        />
      </View>
      <View style={{ flex: 2 }}>
        <View>
          <Text style={styles.price}>{`R$ ${price}`}</Text>
        </View>
        <View>
          <Text style={styles.name}>{name}</Text>
        </View>
        <View>
          <Text style={styles.stock}>Quantidade: {stock}</Text>
        </View>
      </View>
      <View style={{ flex: 3 }}>
        <Button onPress={() => {}} title="Adicionar" />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column",
    width: "100%",
    backgroundColor: "#fff",
  },
  image: {
    width: "100%",
    height: 200,
  },
  price: {
    fontSize: 24,
    marginHorizontal: "1%",
  },
  name: {
    fontFamily: "Anton_400Regular",
    fontSize: 20,
    marginHorizontal: "1%",
    opacity: 0.4,
  },
  stock: {
    fontSize: 15,
    paddingHorizontal: "2%",
    opacity: 0.4,
  },
});
