import { Product } from "../interfaces/product";
import { BancoSql } from "../core/db";
import React, { useState } from "react";

const state: any = {
  products: [],
  status: "",
};
const db = BancoSql();

async function validation(params: Product) {
  let teste: any = [];
  //   await db.transaction((tx) => {
  //     tx.executeSql("delete from car");
  //   });

  await list().then((result: any) => {
    teste.push(result);
    teste.map((item: any, index: number) => item);
    teste.forEach((item: any) => {
      console.log(item);
    });
    for (let items of teste) {
      console.log(items.id);
      if (items.id === params.id) {
        console.log(state.status);
        state.status(true);
        //alert("No");
      }
    }
  });
  console.log(state.status);
}

export async function list() {
  await db.transaction((tx) => {
    tx.executeSql(
      "select * from car",
      [],
      (_, { rows }) => (state.products = rows)
    );
  });
  console.log(state.products);

  return state.products;
}
export async function update(params: Product) {
  await db.transaction((tx) => {
    tx.executeSql(
      "update car set name = ?, " +
        " name = ?, image = ?, price = ?,stock = ?,createdAt = ?" +
        " where id = ?;",
      [
        params.name.toString(),
        params.image.toString(),
        params.price,
        params.stock,
        params.createdAt.toString(),
        params.id,
      ]
    );
  });
}

export async function add(params: Product) {
  let validate: any = await validation(params);
  console.log(validate);
  if (validate == "true") {
    return console.log("Produto ja esta no carinho");
  }
  const add = {
    id: params.id,
    name: params.name,
    price: params.price,
    image: params.image,
    stock: 1,
  };
  if (params.id === null) {
    return false;
  }
  await db.transaction((tx) => {
    tx.executeSql(
      "insert into car (id, name,image, price,stock,createdAt) values (?,?,?,?,?,?)",
      [
        params.id,
        params.name.toString(),
        params.image.toString(),
        params.price,
        params.stock,
        params.createdAt.toString(),
      ]
    );
  });
}
