import React from "react";
import { Image, StyleSheet, Text, TouchableOpacity, View } from "react-native";

export default function Index(props: any) {
  function filterDesc(desc: any) {
    if (desc.length < 20) {
      return desc;
    }
    return `${desc.substring(0, 18)}...`;
  }
  return (
    <TouchableOpacity style={styles.container}>
      <View style={styles.img}>
        <Image source={{ uri: props.img }} style={styles.shoesimg} />
      </View>
      <View style={styles.title}>
        <Text style={styles.shoestext}>{filterDesc(props.children)}</Text>
      </View>
      <View style={styles.saldo}>
        <Text style={styles.shoestext}>{props.text}</Text>
      </View>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  container: {
    paddingVertical: "2%",
    flexDirection: "row",
    flexWrap: "wrap",
  },
  shoestext: {
    fontSize: 16,
  },
  shoesimg: {
    width: 50,
    height: 50,
  },
  car: {
    flexDirection: "row",
    flexWrap: "wrap",
  },
  img: {
    alignSelf: "flex-start",
  },
  title: {
    alignSelf: "center",
  },
  saldo: { opacity: 0.4, alignSelf: "flex-end" },
  stock: {},
});
