import AsyncStorage from "@react-native-async-storage/async-storage";
import { Product } from "../interfaces/product";
import { BancoSql } from "../core/db";
const state: any = {
  products: [],
};

export async function save(params: Product) {
  let pord: any;
  state.products == null
    ? (pord = [state.products])
    : (pord = [...state.products]);

  validation(params, state.products);
  const add = {
    id: params.id,
    name: params.name,
    price: params.price,
    image: params.image,
    stock: 1,
  };
  pord.splice(0, 0, add);
  await AsyncStorage.setItem("car", JSON.stringify(pord));
}

export async function load() {
  const result: any = await AsyncStorage.getItem("car");
  state.products = JSON.parse(result);
}

