  //     //alert("Produto jÃ¡ existe");
  //     // }
  //   });
  list();
}

export function list() {
  const db = BancoSql();
  db.transaction((tx) => {
    tx.executeSql("select * from car");
  });
  return db;
}
export function update(params: Product) {
  const db = BancoSql();
  db.transaction((tx) => {
    tx.executeSql(
      "update car set name = ?, " +
        " name = ?, image = ?, price = ?,stock = ?,createdAt = ?" +
        " where id = ?;",
      [
        params.name.toString(),
        params.image.toString(),
        params.price,
        params.stock,
        params.createdAt.toString(),
        params.id,
      ]
    );
  });
}

export function add(params: Product) {
  const db = BancoSql();
  if (params.id === null) {
    return false;
  }
  db.transaction((tx) => {
    tx.executeSql(
      "insert into car (id, name,image, price,stock,createdAt) values (?,?,?,?,?,?)",
      [
        params.id,
        params.name.toString(),
        params.image.toString(),
        params.price,
        params.stock,
        params.createdAt.toString(),
      ]
    );
    tx.executeSql("select * from car", [], (_, { rows }) =>
      console.log(JSON.stringify(rows))
    );
  });
}
