import React from "react";
import {
  Button,
  Image,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";

export default function Index(props: any) {
  function filterDesc(desc: any) {
    if (desc.length < 20) {
      return desc;
    }
    return `${desc.substring(0, 18)}...`;
  }
  return (
    <View style={styles.container}>
      <View style={styles.img}>
        <Image source={{ uri: props.img }} style={styles.shoesimg} />
      </View>
      <View style={styles.title}>
        <Text style={styles.shoestext}>{filterDesc(props.children)}</Text>
        <Text style={styles.saldo}>{props.text}</Text>
        <View style={{ flexDirection: "row" }}>
          <Button onPress={() => {}} title="-" style={styles.buttons} />
          <TextInput style={styles.stock} value={props.stock} />
          <Button onPress={() => {}} title="+" />
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "row",
    paddingTop: 20,
  },
  shoestext: {
    fontSize: 16,
  },
  shoesimg: {
    width: 75,
    height: 75,
    borderRadius: 4,
  },

  img: {},
  title: {
    flexDirection: "column",
    paddingLeft: 20,
  },
  saldo: { paddingBottom: 10, paddingTop: 10, color: "red", fontWeight: 300 },
  stock: {
    width: "50px",
    height: "30px",
    opacity: 0.5,
  },
  button: {
    height: "30px",
    color: "#fff",
  },
});
