import React, { useEffect, useState } from "react";
import { View, Text, ScrollView, StyleSheet } from "react-native";
import { Product } from "../../interfaces/product";
import { list } from "../../service";
import Cart from "../../component/cart";

export default function index() {
  let result: any;
  const [product, setProducts] = useState<Product[]>([]);
  const [total, setTotal] = useState();
  async function handleLoad() {
    const result = await list();
    setProducts(result);
  }
  async function handleTotal() {
    const result = await list();
    for (let index = 0; index < result.length; index++) {
      let totalQtde: any = (totalQtde =
        parseFloat(result[index].price) * result[index].stock);
      console.log(totalQtde);

      // let valor: any = (totalQtde =
      //   parseFloat(totalQtde) + parseFloat(result[index].price));
      // console.log(valor);
      await setTotal(totalQtde);
    }
  }
  useEffect(() => {
    handleLoad();
    handleTotal();
  }, []);
  return (
    <View style={styles.container}>
      <View style={styles.total}>
        <View style={styles.titleValor}>
          <Text>Total</Text>
        </View>
        <View style={styles.valor}>
          <Text>{`R$ ${total}`}</Text>
        </View>
      </View>
      <ScrollView>
        <View style={styles.viewScroll}>
          {product?.map((item: Product) => (
            <Cart
              key={item.id}
              id={item.id}
              image={item.image}
              price={item.price}
              stock={item.stock}
            >
              {item.name}
            </Cart>
          ))}
        </View>
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  viewScroll: {},
  total: {
    flexDirection: "row",
    justifyContent: "space-around",
  },
  titleValor: {
    alignSelf: "center",
  },
  valor: {
    alignSelf: "flex-end",
    color: "red",
  },
});
