    price: params.price,
    image: params.image,
    stock: parseInt(stack),
  };
  const result = await list();
  let fruits: any;
  result == null ? (fruits = []) : (fruits = result);

  for (let index = 0; index < result?.length; index++) {
    if (result[index].id == id) {
      console.log(result[index]);

      try {
        fruits.filter((item: any) => console.log(item));
        fruits.splice(result[index], 1, add);
      } catch (error) {
        console.log(error);
      }

      const jsonValue = JSON.stringify(fruits);
      await AsyncStorage.setItem("car", jsonValue);
      return state.status;
    } else {
      state.status = "false";
      success("Produto add no carinho");
      return state.status;
    }
  }
}

export async function add(params: Product) {
  let validate: any = await validation(params);
