import { Product } from "../interfaces/product";
import { BancoSql } from "../core/db";
import { Slide, toast, Zoom } from "react-toastify";
import "react-toastify/dist/ReactToastify.min.css";
import React from "react";

const state: any = {
  products: [],
  status: "",
};
const db = BancoSql();

async function validation(params: Product) {
  //   await db.transaction((tx) => {
  //     tx.executeSql("delete from car");
  //   });

  await list().then((result: any) => {
    for (let index = 0; index < result.length; index++) {
      if (result[index].id == params.id) {
        state.status = "true";
        error("Produto ja esta no carinho");
        return;
      }
    }
  });
  return state.status;
}

export async function list() {
  const [items, setItems] = React.useState([{}]);
  await db.transaction((tx) => {
    tx.executeSql(`select * from car; `, [], (_, { rows: { _array } }) =>
      setItems(_array)
    );
  });
  //   await db.transaction((tx) => {
  //     tx.executeSql(
  //       "select * from car",
  //       [],
  //       (_, { rows }) => (state.products = rows)
  //     );
  //   });
  console.log(items);
  if (items === null || items.length === 0) {
    return null;
  }

  return await items;
}
export async function update(params: Product) {
  await db.transaction((tx) => {
    tx.executeSql(
      "update car set name = ?, " +
        " name = ?, image = ?, price = ?,stock = ?,createdAt = ?" +
        " where id = ?;",
      [
        params.name.toString(),
        params.image.toString(),
        params.price,
        params.stock,
        params.createdAt.toString(),
        params.id,
      ]
    );
  });
}

export async function add(params: Product) {
  let validate: any = await validation(params);
  if (validate == "true") return;
  const add = {
    id: params.id,
    name: params.name,
    price: params.price,
    image: params.image,
    stock: 1,
  };
  if (params.id === null) {
    return false;
  }
  await db.transaction((tx) => {
    tx.executeSql(
      "insert into car (id, name,image, price,stock,createdAt) values (?,?,?,?,?,?)",
      [
        params.id,
        params.name.toString(),
        params.image.toString(),
        params.price,
        params.stock,
        params.createdAt.toString(),
      ]
    );
  });

  await success("Salvo com sucesso!");
  await list();
}
function error(msg: String) {
  toast.error(`${msg}`, {
    theme: "dark",
    transition: Zoom,
  });
}
function success(msg: String) {
  toast.success(`'${msg}'`, {
    theme: "dark",
  });
}
